CREATE TABLE `service` (
  `id` int(11) NOT NULL,
  `name` char(100) NOT NULL,
  `price` int(3) NOT NULL,
  `description` text DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Дамп данных таблицы `service`
--

INSERT INTO `service` (`id`, `name`, `price`, `description`) VALUES
(1, 'Классический маникюр без покрытия', 10, 'Классический обрезной маникюр — комфортный вариант ухода для людей, которые стремятся следить за своими руками,ногтями и поддерживать их в идеальном состоянии. Данный маникюр подходит для любой длины ногтей. Наши мастера сделают Вам самый аккуратный маникюр.\r\n\r\nМастер использует средства и инструменты, которые позволяют мягко удалить кутикулу аккуратно и нежно, обрезая лишнюю ткань. Классический маникюр лучше всего делать раз в 10-12 дней.\r\n\r\nИспользуются для классического маникюра наиболее острые инструменты, позволяющие сделать ваши руки и ногти аккуратными и ухоженными. При этом кутикула значительно замедляет свой рост.\r\n\r\nЗатем возможно нанесение любого покрытия с разными дизайнами (лечебное покрытие, френч, долговременное покрытие гель лак, классический лак).'),
(2, 'Покрытие гель-лак', 15, 'Гель-лак – это гибрид обычного лака для ногтей и геля для наращивания, поэтому он и имеет такое название. Материал собрал в себе лучшие качества обоих покрытий: цвет и стойкость до 2-3 недель.\r\n\r\nНа ногтевую пластину он ложится так же просто, как и обычный лак, что дает возможность без труда использовать его в домашних условиях. \r\n\r\nЗа счет того, что гель-лак вобрал в себя свойства геля, он не только обладает стойкостью, позволяющей ходить покрытием 2-3 недели, но и придает ноготкам дополнительную жесткость. Гель-лак укрепляет ногтевую пластину, что позволяет обладательницам тонких и ломких ногтей отрастить длину.\r\n\r\nКроме того, женщинам отлично известна проблема смазанного лака и долгого мучительного ожидания пока он высохнет, а с гель-лаком об этом можно забыть. \r\n\r\nГель-лак сушат в специальной лампе, на что в среднем уходит 0,5-1 минута. Конечно, покупка лампы ассоциируется с дополнительными тратами, но стоит отметить, что лампа для полимеризации гель-лака прослужит вам от 3 до 5 лет! Приобретя ее всего 1 раз, вы будете ходить с безупречным маникюром на протяжении нескольких лет. '),
(3, 'Укрепление IBX', 12, 'IBX System – это профессиональная салонная процедура, а точнее, первая и единственная в своем роде лечебная система для проблемных ногтей.\r\n\r\nНазвание произошло от трех слов: Iceberg - айсберг, Big - огромный, X - цифра десять на латинице. А расшифровать смысл этого сочетания просто: препараты работают подобно айсбергу, основная часть которого скрыта от глаз. То есть процесс восстановления происходит внутри ногтевой пластины и распространяется, разумеется, на все 10 ногтей.\r\n\r\nИменно внутреннее действие и является главным отличием IBX от множества укрепляющих покрытий, представленных на рынке. Препарат сцепляется с поверхностью ногтевой пластины, проникает в структуру и становится ее частью. Поэтому он не снимается жидкостью для снятия акрила и гель-лака, а отрастает вместе с натуральным ногтем.'),
(4, 'Дизайнерское покрытие', 5, NULL),
(5, 'Французский маникюр', 5, 'Французский маникюр — один из самых распространённых и популярных видов дизайна ногтей. Классический французский маникюр выглядит следующим образом: ногтевая пластина покрывается неярким бежевым или пастельно-розовым тоном лака, а на кончик ногтя по форме полумесяца наносится белый цвет.');

